import React from "react";
import { Switch } from "react-router-dom";
import {
  Link,
  MemoryRouter as Router,
  Route,
  Redirect,
  withRouter,
  useHistory,
} from "react-router-dom";

const PageOne = () => {
  return <h3>Page One</h3>;
};

const PageTwo = () => {
  return <h3>Page Two</h3>;
};

const PageThree = () => {
  return <h3>Page Three</h3>;
};

const PageFour = () => {
  return <h3>Page Four</h3>;
};

const Wizard = withRouter(({ children }) => {
  const steps = React.Children.toArray(children);
  return (
    <>
      <Switch>
        {steps.map((step, index) => {
          const { name } = step.props;
          return (
            <Route
              key={index}
              path={[`/page-${index}`, `/${name}`]}
              component={() => step}
            />
          );
        })}
      </Switch>
    </>
  );
});

const Step = ({ children }) => children;

export default function MyWizard() {
  let history = useHistory();
  return (
    <Router>
      <div>
        <Link to={`/page-0`}>Page-0</Link>
      </div>
      <div>
        <Link to={`/screen-two`}>screen-two</Link>
      </div>
      <div>
        <button onClick={() => history.push(`/screen-one`)}>Page-0</button>
      </div>
      <section className="App">
        <Wizard>
          <Step name="screen-one">
            <PageOne />
          </Step>
          <Step name="screen-two">
            <PageTwo />
          </Step>
          <Step name="screen-three">
            <PageThree />
          </Step>
          <Step name="screen-four">
            <PageFour />
          </Step>
        </Wizard>
        {/* <Router>
      <Link to={`/page-0`}>Page One</Link>
      <Switch>
        <Route path={`/page-0`} component={PageOne} />
        <Route path={`/page-1`} component={PageTwo} />
        <Route path={`/page-2`} component={PageThree} />
        <Route path={`/page-3`} component={PageFour} />
      </Switch>
    </Router> */}
      </section>
    </Router>
  );
}
