import React from "react";
import { Link, MemoryRouter as Router, Route, Redirect } from "react-router-dom";

const PageOne = () => {
  return <h3>Page One</h3>;
};

const PageTwo = () => {
  return <h3>Page Two</h3>;
};

const PageThree = () => {
  return <h3>Page Three</h3>;
};

const PageFour = () => {
  return <h3>Page Four</h3>;
};


const Wizard = ({ children }) => {
  const steps = React.Children.toArray(children);
  return (
    <Router>
      <Template path="/:currentPath" steps={steps} />
      <Redirect from="/" to={steps[0].props.path} noThrow/>
    </Router>
  );
};

export default function MyWizard() {
  return (
    <section className="App">
      <Router>
        <Redirect from="/" to={steps[0].props.path} noThrow/>
        <Route exact path="/" component={PageOne} />
        <Route exact path="/page-two" component={PageTwo} />
        <Route exact path="/page-three" component={PageThree} />
        <Route exact path="/page-four" component={PageFour} />
      </Router>
    </section>
  );
}
