import React from "react";
import {
  Link,
  MemoryRouter as Router,
  Route,
  Redirect,
} from "react-router-dom";

const PageOne = () => {
  return <h3>Page One</h3>;
};

const PageTwo = () => {
  return <h3>Page Two</h3>;
};

const PageThree = () => {
  return <h3>Page Three</h3>;
};

const PageFour = () => {
  return <h3>Page Four</h3>;
};

const Wizard = ({ children }) => {
  const steps = React.Children.toArray(children);
  console.log(steps);
  return (
    <Router initialIndex={0}>
      {/* <Redirect to="/0" /> */}
      {steps.map((step, index) => (
          <Route key={index} exact path={`/page-${index}`} component={step}  />
        ))}
    </Router>
  );
};

const Step = ({ children }) => children;

export default function MyWizard() {
  return (
    <section className="App">
      <Wizard>
        <Step>
          <PageOne />
        </Step>
        <Step>
          <PageTwo />
        </Step>
        <Step>
          <PageThree />
        </Step>
        <Step>
          <PageFour />
        </Step>
      </Wizard>
    </section>
  );
}
