import React from "react";
import { Redirect } from "react-router-dom";
import {
  Link,
  withRouter,
  useHistory,
} from "react-router-dom";
import { FlowContext } from "./flowProvider";


const Controls = withRouter(() => {
    const [goBack, goNext, screens, setScreens, current, setCurrent] = React.useContext(FlowContext);
    const history = useHistory();
    const goToNextScreen = () => {
        goNext();
        history.push(`/screen-${current}`);
    }
    const goToPreviousScreen = () => {
        goBack();
        history.push(`/screen-${current}`);
    }
    return (
      <>
        <div>
          <Link to={`/screen-0`}>Link To Screen-0</Link>
        </div>
        <div>
          <Link to={`/transaction-tracker`}>Link to transaction-tracker</Link>
        </div>
        <div>
          <button onClick={() => history.push(`/transaction-page`)}>History push to Transaction-Page</button>
          <button onClick={() => goToNextScreen}>Next</button>
          <button onClick={() => goToPreviousScreen}>Back</button>
        </div>
      </>
    );
  });

  export default Controls;
