import React from "react";
import { Switch } from "react-router-dom";
import {
  Link,
  MemoryRouter as Router,
  Route,
  Redirect,
  withRouter,
  useHistory,
} from "react-router-dom";

const PageOne = () => {
  return <h3>Screen Zero</h3>;
};

const PageTwo = () => {
  return <h3>Screen One</h3>;
};

const PageThree = () => {
  return <h3>Screen Two</h3>;
};

const PageFour = () => {
  return <h3>Screen Three</h3>;
};

const Flow = withRouter(({ children }) => {
  const steps = React.Children.toArray(children);
  const history = useHistory();
  return (
    <>
      <div>
        <Link to={`/page-0`}>Link To Screen-0</Link>
      </div>
      <div>
        <Link to={`/screen-two`}>Link to Screen-two</Link>
      </div>
      <div>
        <button onClick={() => history.push(`/screen-one`)}>History push to Screen-one</button>
      </div>
      <Switch>
        {steps.map((step, index) => {
          const { name } = step.props;
          return (
            <Route
              key={index}
              path={[`/page-${index}`, `/${name}`]}
              component={() => step}
            />
          );
        })}
      </Switch>
    </>
  );
});

const Screen = ({ children }) => children;

export default function MyFlowSetup() {
  return (
    <Router>
      <section className="App">
        <Flow>
          <Screen name="screen-zero">
            <PageOne />
          </Screen>
          <Screen name="screen-one">
            <PageTwo />
          </Screen>
          <Screen name="screen-two">
            <PageThree />
          </Screen>
          <Screen name="screen-three">
            <PageFour />
          </Screen>
        </Flow>
      </section>
    </Router>
  );
}
